function dropHandler(e){e.preventDefault();if(document.querySelector(".file-button-wrapper").classList.toggle("drag-over",!1),e.dataTransfer.files)if(e.dataTransfer.files.length>1)alert("Only upload 1 file");else{document.getElementById("file").files=e.dataTransfer.files,generatePreview(e.dataTransfer.files[0])}}function dragOverHandler(e){e.preventDefault()}function dragEnterHandler(e){document.querySelector(".file-button-wrapper").classList.toggle("drag-over",!0)}function dragLeaveHandler(e){document.querySelector(".file-button-wrapper").classList.toggle("drag-over",!1)}function selectHandler(e){let r=e.files[0];if(generatePreview(r),!r)return!1}